"""
A string is given (very large). Print the word that occurs most often in
    this line. If there are several such words, print the last one.

The problem must be solved using a dictionary.
"""


def count_words(text: str) -> dict:
    """
    Creates dict with words and number of meetings
    :param text:
    :return: dict k -> word; v -> value
    """
    list_words = get_words_from_text(text)      # get list with all words
    dict_word_number = dict()
    for word in list_words:              # add to dict, where V - number
        if word in dict_word_number:     # how many times the word meet
            dict_word_number[word] = dict_word_number.get(word) + 1
        else:
            dict_word_number[word] = 1
    return dict_word_number
    
    
    
def get_words_from_text(text: str) -> list:
    """
    Check every letter, if next one is not letter - save letters before
        as word and to the set
    :return: set with unique words
    """
    # For Regex -> \b\w*[\’\-\w]*\b, decided to delete code with it.
    # This should be faster
    words = []
    word = ""
    for char in text:
        if char.isalpha() or char == "’" or char == "-":
            word += char.lower()
        elif word:
            words.append(word)
            word = ""
    return words


