    text = string.replace("\'", "").replace("\"", "").lower()
    words = text.replace(",", "").replace(".", "").split()
    
    # 1 - saves info, doesn't use dict
    max_met = 0
    word_met = ""
    dict_words = dict()
    
    for word in words:
        met = words.count(word)
        
        if word not in dict_words:
            dict_words[word] = met
            
        if max_met <= met:
            max_met = met
            word_met = word
    print(word_met, max_met)

    # 3 - Improved #2, 10x faster, saves info, uses dict
    dict_words_3 = dict()
    for word in set(words):
        dict_words_3[word] = words.count(word)

    dict_numb = {v: k for k, v in dict_words_3.items()}
    
    max_ = max(dict_numb.keys())
    print(dict_numb.get(max_))
    
